import logo from "../images/Logo.svg";
import { render } from "react-dom";

const App = (props) => {
  const handleKeyDown = (event) => {
    console.log("A key was pressed", event.keyCode);
  };

  React.useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);

    // cleanup this component
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, []);

  return (
    <div className="container">
      <h1>Welcome to the Keydown Listening Component</h1>
    </div>
  );
};

function Main(props) {
  const handleEditAvatarClick = (e) => {
    console.log("CLICK");
  };
  React.useEffect(() => {
    document
      .querySelector("profile__avatar-edit")
      .addEventListener("click", () => {
        console.log("Clicked");
      });
  }, []);

  return (
    <main className="content">
      {/* Profile Section */}
      <section className="profile">
        <form className="profile__info-area">
          <button
            className="profile__avatar-edit"
            aria-label="edit-avatar"
            type="button"
          />
          <img
            id="avatar Image"
            alt="Avatar Image for User"
            className="profile__avatar"
            src="./images/jacques.png"
          />
          <div className="profile__info">
            <h1 className="profile__name" />
            <p className="profile__title" />
          </div>
          <button
            className="profile__editbtn button"
            aria-label="Edit"
            type="button"
            name="edit_btn"
          />
        </form>
        <button
          aria-label="add"
          className="profile__addbtn button"
          type="button"
          name="add_btn"
        />
      </section>
      {/* Grid Section */}
      <section className="grid">
        <ul className="grid__list">{/* Generate the Cards here */}</ul>
      </section>
    </main>
  );
}

export default Main;
