import logo from "../images/Logo.svg";
import { render } from "react-dom";
import React from "react";

function handleEditAvatarClick(e) {
  e.preventDefault();
  const newCont = document
    .querySelector(".popup__avatar")
    .classList.add("popup_visible");
  console.log("Handle Avatar");
}
function handleEditProfileClick(e) {
  e.preventDefault();
  const newCont = document
    .querySelector(".popup_edit")
    .classList.add("popup_visible");
  console.log("Handle Profile");
}

function handleAddPlaceClick(e) {
  e.preventDefault();
  const newCont = document
    .querySelector(".popup_card")
    .classList.add("popup_visible");
  console.log("Handle Add Place");
}

function Main(props) {
  return (
    <main className="content">
      {/* Profile Section */}
      <section className="profile">
        <form className="profile__info-area">
          <button
            className="profile__avatar-edit"
            aria-label="edit-avatar"
            type="button"
            onClick={handleEditAvatarClick}
          />
          <img
            id="avatar Image"
            alt="Avatar Image for User"
            className="profile__avatar"
            src="./images/jacques.png"
          />
          <div className="profile__info">
            <h1 className="profile__name" />
            <p className="profile__title" />
          </div>
          <button
            className="profile__editbtn button"
            aria-label="Edit"
            type="button"
            name="edit_btn"
            onClick={handleEditProfileClick}
          />
        </form>
        <button
          aria-label="add"
          className="profile__addbtn button"
          type="button"
          name="add_btn"
          onClick={handleAddPlaceClick}
        />
      </section>
      {/* Grid Section */}
      <section className="grid">
        <ul className="grid__list">{/* Generate the Cards here */}</ul>
      </section>
    </main>
  );
}

export default Main;
