import logo from "../images/Logo.svg";
import React, { useState, useEffect, createContext } from "react";
import jacques from "../images/jacques.png";
import api from "../utils/Api";

function Card(props) {
  console.log(props);
  return (
    <li class="grid__card">
      <button
        class="grid__btn_del button"
        type="button"
        aria-label="delete"
      ></button>

      <button class="grid__btn_popup" type="button" aria-label="view image">
        <img class="grid__image" src="{props.link}" alt="Desc" />
      </button>
      <div class="grid__card-text">
        <h2 class="grid__caption">{props.title}</h2>
        <div class="grid__likes">
          <button
            class="grid__heart button"
            type="button"
            aria-label="like"
          ></button>
          <p class="grid__like-count"></p>
        </div>
      </div>
    </li>
  );
}

export default Card;
