import logo from "../images/Logo.svg";
import { render } from "react-dom";
import React from "react";

function Main(props) {
  React.useEffect(() => {
    function handleEditAvatarClick(e) {
      console.log("Clicked");
    }

    // list of actions inside one hook
    document
      .querySelector("profile__avatar-edit")
      .addEventListener("click", handleEditAvatarClick);

    // we're returning a function that remove our effects
    return () => {
      document.removeEventListener("click", handleEditAvatarClick);
    };
  });

  return (
    <main className="content">
      {/* Profile Section */}
      <section className="profile">
        <form className="profile__info-area">
          <button
            className="profile__avatar-edit"
            aria-label="edit-avatar"
            type="button"
          />
          <img
            id="avatar Image"
            alt="Avatar Image for User"
            className="profile__avatar"
            src="./images/jacques.png"
          />
          <div className="profile__info">
            <h1 className="profile__name" />
            <p className="profile__title" />
          </div>
          <button
            className="profile__editbtn button"
            aria-label="Edit"
            type="button"
            name="edit_btn"
          />
        </form>
        <button
          aria-label="add"
          className="profile__addbtn button"
          type="button"
          name="add_btn"
        />
      </section>
      {/* Grid Section */}
      <section className="grid">
        <ul className="grid__list">{/* Generate the Cards here */}</ul>
      </section>
    </main>
  );
}

export default Main;
