import React from "react";

function PopupWithForm() {
  const [position, setPosition] = React.useState({ top: 0, left: 0 });

  React.useEffect(() => {
    function handleMouseMove(event) {
      setPosition({
        top: event.pageY,
        left: event.pageX,
      });
    }

    // list of actions inside one hook
    document.addEventListener("mousemove", handleMouseMove);
    document.body.classList.add("no-cursor");

    // we're returning a function that remove our effects
    return () => {
      document.body.classList.remove("no-cursor");
      document.removeEventListener("mousemove", handleMouseMove);
    };
  });

  return (
    <div classname="popup__card popup">
      <div classname="popup__form popup__card_form">
        <form
          classname="popup__card_form-selector form"
          id="addForm"
          name="addForm"
        >
          <h3 classname="popup__card_heading">New Place</h3>
          <input
            type="text"
            classname="popup__card_input form_input"
            id="inputPlace"
            name="placeName"
            placeholder="Title"
            defaultvalue
            minLength="{2}"
            maxLength="{30}"
            required
          />
          <span
            classname="popup__form_input_type_active inputPlace-error error"
            name="inputPlace-error"
          >
            <input
              type="url"
              classname="popup__card_input form_input"
              id="inputFile"
              name="placeFileName"
              placeholder="Image link"
              defaultvalue
              required
            />
            <span
              classname="popup__form_input_type_active inputFile-error error"
              name="inputFile-error"
            >
              <button
                type="submit"
                classname="popup__card_submit-disabled popup__card_submit form__submit"
                value="Save"
                id="addFormSubmit"
                disabled
              >
                Create
              </button>
              <button
                type="button"
                classname="popup__close button"
                aria-label="close"
                name="close_card"
              ></button>
            </span>
          </span>
        </form>
      </div>
    </div>
  );
}

export default PopupWithForm;
