import logo from "../images/Logo.svg";
import React, { useState, useEffect, createContext } from "react";
import jacques from "../images/jacques.png";
import api from "../utils/Api";

function Main(props) {
  const [userName, setUserName] = React.useState("");
  const [userTitle, setUserTitle] = React.useState("");
  const [userAvatar, setUserAvatar] = React.useState("");

  useEffect(() => {
    api
      .getUserInfo()
      .then((res) => {
        setUserName(res);
        setUserTitle(res);
        setUserAvatar(res);
      })
      .then((res) => {
        console.log(res.userName);
      })
      // .then(() => {
      //     api.getCardList()
      //     .then((res) => {
      //         setCards(
      //             res.map((card) => ({
      //                 name: card.name,
      //                 link: card.link,
      //                 _id: card._id,
      //                 likes: card.likes,
      //                 owner: card.owner,
      //         })))
      //     })
      //     .catch((err) => console.log(err));
      // })
      .catch((err) => console.log(err));
  }, []);

  return (
    <main className="content">
      {/* Profile Section */}
      <section className="profile">
        <form className="profile__info-area">
          <button
            className="profile__avatar-edit"
            aria-label="edit-avatar"
            type="button"
            onClick={props.handleEditAvatarClick}
          />
          <img
            id="avatar Image"
            alt="Avatar Image for User"
            className="profile__avatar"
            src="./images/jacques.png"
          />
          <div className="profile__info">
            <h1 className="profile__name" />
            <p className="profile__title" />
          </div>
          <button
            className="profile__editbtn button"
            aria-label="Edit"
            type="button"
            name="edit_btn"
            onClick={props.handleEditProfileClick}
          />
        </form>
        <button
          aria-label="add"
          className="profile__addbtn button"
          type="button"
          name="add_btn"
          onClick={props.handleAddPlaceClick}
        />
      </section>
      {/* Grid Section */}
      <section className="grid">
        <ul className="grid__list">{/* Generate the Cards here */}</ul>
      </section>
    </main>
  );
}

export default Main;
